require_relative "longest_palindrome.rb"
require "test/unit"

class PalindromeTest < Test::Unit::TestCase

  def test_simplecase
    assert_equal(longest_palindrome("racecar"),"racecar")
  end

  def test_caps_and_puncuation
    assert_equal(longest_palindrome("Ra-Ce,CA r"),"racecar")
  end

  def test_super_long
    p = ''
    assert_equal(longest_palindrome(p), 'ranynar')
  end

end